import React from 'react';
import { Input,Rate,Button,Form,message  } from 'antd';
import { Link } from 'react-router-dom';
import axios from 'axios';

const { TextArea } = Input;
const FormItem = Form.Item;
const _success = () => {
    message.success('New comment successfully submit');
  };
  const _error = () => {
    message.error('New comment failed');
  };

export default class ArticleDetailComment extends React.Component {
    componentDidMount() {
      }

    render() {
        const { getFieldDecorator } = this.props.form;
            return (
                <div className="article-comment-box">
                <Form onSubmit={this.handleSubmit} layout="inline">
                    <FormItem>
                    <div className="article-comment-box-rate">
                        {getFieldDecorator('rate')(
                            <Rate allowHalf defaultValue={2.5}/>
                        )}
                    </div>
                    </FormItem>
                    <FormItem>
                     <div className="article-comment-box-coment">
                        {getFieldDecorator('comments')(
                            <TextArea placeholder="comments" autosize={{ minRows: 2, maxRows: 6 }} />
                        )}
                     </div>
                     </FormItem>
                     <div  className="article-comment-box-button">
                     <Button type="primary">submit</Button>&nbsp;&nbsp;&nbsp;
                    <Link to="/blog"><Button>cancel</Button></Link></div>
                </Form>
                </div>
            );
        }
}
