import React from 'react';
import axios from 'axios';

export default class Presentation extends React.Component {

    constructor() {
        super();
        this.state = {
            load:false,
            resentation:[]
            }
    }

    componentDidMount() {
        const _this = this;
        axios.post('https://2xhclrt4yl.execute-api.us-east-1.amazonaws.com/stage/getpresentationall')
        .then(function (response) {
            _this.setState({
            load:true,
            resentation: response.data
            });
        }).catch(function (error) {
            console.log(error);
            _this.setState({
            error: error
            })
        })
    }

    render() {
        var load = this.state.load;
        if(load){
        return (
            <div className="main">
            <div className="main-container">
                <Row>
                    <Col xs={24} sm={24} md={18}>
                    <ArticleArea articlescategory={this.state.articlescategory}/>
                    </Col>
                    <Col xs={0} sm={0} md={6}>
                    <RightSider categoryList={this.state.categoryList} setCategory={this.setCategory}/>
                    </Col>
                </Row>
            </div>
            </div>
        );
        }else{
        return (
            <div className="main">
            <div className="main-container-spinner">
                <Spinner />
                </div>
            </div>
        )
        }
    }
     
}
