import React from 'react';
import ScrollEvent from 'react-onscroll';
import { Icon } from 'antd';

export default class ArticleItem extends React.Component {
  constructor(props) {
    super(props);

    this.handleScrollCallback = this.handleScrollCallback.bind(this);
  }

  handleScrollCallback() {
      console.log("A scroll event occurred!");
  }

  render() {
    return (
      <div>
        <ScrollEvent handleScrollCallback={this.handleScrollCallback}>
        <div className="blog-article-item">
          <div className="blog-article-item-title">{this.props.title}</div>
          <div className="blog-article-item-time"><Icon type="calendar" /> {this.props.time}</div>
          <div className="blog-article-item-desc">
            <p>
              {this.props.content}
            </p>
          </div>
        </div>
        </ScrollEvent>
      </div>
    );
  }
}
