import React from 'react';
import axios from 'axios';
import { Icon } from 'antd';
import { Link } from 'react-router-dom';

export default class CategoryCard extends React.Component {

  constructor() {
    super();
    this.state = {
      categoryList: []
    }
  }

  componentDidMount() {
    const _this = this;
    axios.post('https://jhuivh4yuc.execute-api.us-east-1.amazonaws.com/stage/getarticlebyname',{"name": "test2"})
      .then(function (response) {
        console.log(response.data);
        _this.setState({
          categoryList: response.data
        });
      }).catch(function (error) {
        console.log(error);
        _this.setState({
          error: error
        })
      })
  }

  render() {
    const categoryLinkList = this.state.categoryList.sort((a, b) => b.sum - a.sum).map(
      item => <Link key={item.id} to={`/blog/category/${item.url}`}><li key={item.id}>{item.name}<span>{item.sum}</span></li></Link>);

    return (
      <div className="rightsider-card">
        <div className="rightsider-card-title"><Icon type="bars" /> 分类</div>
        <div className="rightsider-card-content">
          <ul>
            {categoryLinkList}
          </ul>
        </div>
      </div>
    );
  }
}
