import React from 'react';
import axios from 'axios';
import HomeCard from './homecard';
import '../../../css/home/homecard.css';

export default class HomeCardArea extends React.Component {
  constructor() {
    super();
    this.state = {
      articleCard: null,
      demoCard: null,
      aboutCard: null,
    };
  }
  articleCard = <HomeCard key={1} cardId={1} cardUrl="/blog" cardName="Blog" />;
  demoCard = <HomeCard key={2} cardId={2} cardUrl="/demo" cardName="Demo" />;
  aboutCard = <HomeCard key={3} cardId={3} cardUrl="/about" cardName="About Me" />;

  render() {
    return (
      <div className="home-card-area">
        {this.state.articleCard}
        {this.state.demoCard}
        {this.state.aboutCard}
      </div>
    );
  }
}
