import React, { Component } from 'react';
import { EditorState, convertToRaw } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import draftToHtml from 'draftjs-to-html';
import { Form,Button,Input,Icon,Divider,Select } from 'antd';
import '../../../css/blog/articlepost.css';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';

const FormItem = Form.Item;
const Option = Select.Option;

class ArticlePost extends Component {

    constructor() {
      super();
      this.state = {
        editorState: EditorState.createEmpty(),
      }
    }

    handleChange(value) {
      console.log(`selected ${value}`);
    }

    onEditorStateChange = (editorState) => {
      this.setState({
        editorState,
      });
    };

    handleSubmit = (e) => {
      e.preventDefault();
      this.props.form.validateFields((err, values) => {
          if (!err) {
              const title = values.title;
              const content = draftToHtml(convertToRaw(this.state.editorState.getCurrentContent()));
            }
      })
  }

  render() {
    const { getFieldDecorator } = this.props.form;
    const {  editorState } = this.state;

    return (
      <div className="main">
        <div className="main-container-post">
          <div className="archive-list-area">
            <div className="archive-post">
              <Form onSubmit={this.handleSubmit} className="login-form">
                  <Divider>Title</Divider>
                  <FormItem>
                    {getFieldDecorator('title', {
                      rules: [{ required: true, message: 'Please input title!' }],
                    })(
                      <Input prefix={<Icon type="edit" style={{ color: 'rgba(0,0,0,.25)' }} />} placeholder="Titile" />
                    )}
                  </FormItem>
                  <FormItem>
                        <Select defaultValue="lucy" style={{ width: 120 }} onChange={handleChange}>
                          <Option value="jack">Jack</Option>
                          <Option value="lucy">Lucy</Option>
                          <Option value="disabled" disabled>Disabled</Option>
                          <Option value="Yiminghe">yiminghe</Option>
                        </Select>
                  </FormItem>
                  <Divider>Contnent</Divider>
                  <FormItem>
                    {getFieldDecorator('content', {
                      rules: [{ required: true, message: 'Please input content!' }],
                    })(
                      <Editor
                          editorState={editorState}
                          wrapperClassName="wrapper-class"
                          editorClassName="editor-class"
                          onEditorStateChange={this.onEditorStateChange}
                          />
                      )}
                  </FormItem>
                  <FormItem>
                  <div className="archive-button">
                    <Button type="primary" htmlType="submit" >submit</Button>&nbsp;&nbsp;&nbsp;
                    <Button>cancel</Button>
                  </div>
                  </FormItem>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
ArticlePost = Form.create()(ArticlePost);

export default ArticlePost;
