import React, { Component } from 'react';
import { EditorState, convertToRaw, ContentState } from 'draft-js'; 
import { Editor } from 'react-draft-wysiwyg'; 
import draftToHtml from 'draftjs-to-html'; 
import htmlToDraft from 'html-to-draftjs';
import '../../../css/blog/articlepost.css';

import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';

class ArticlePost extends Component {

  render() {
    const { getFieldDecorator } = this.props.form;
    const {  editorState, contentState } = this.state;
    const formItemLayout = {
        labelCol: {
            xs: { span: 24 },
            sm: { span: 6 },
        },
        wrapperCol: {
            xs: { span: 24 },
            sm: { span: 16 },
        },
    };

    return (
      <div className="main">
        <div className="main-container">
          <div className="archive-list-area">
            <div className="archive-list">

                <Form className='container333'>
                    <FormItem label="请输入主标题:" {...formItemLayout}>
                        {getFieldDecorator('title', {
                            rules: [{
                                required: true, message: '请输入主标题名称',
                            }],
                        })(
                            <Input placeholder="请输入主题" />
                        )}
                    </FormItem>
                    <FormItem label="内容编辑器:" {...formItemLayout}>
                        {getFieldDecorator('content', {
                            validateTrigger:'onBlur',
                            rules: [
                                { required: true},
                                {validator:(rule,value,callback)=>{
                                    if (value.isEmpty()) {
                                             callback('请输入正文内容')
                                           } else {
                                             callback()
                                           }
                                    }
                                 }
                            ]
                        })(<Editor
                                editorState={editorState}
                                onEditorStateChange={this.onEditorStateChange}
                                onContentStateChange={this.onContentStateChange}
                                wrapperClassName="wrapper-class"
                                editorClassName="editor-class"
                                toolbarClassName="toolbar-class"
                            />
                        )}
                    </FormItem>  
                    <div className='footer11'>
                        <div className='footer-right'>
                           每5分钟保存一次
                        </div>
                    </div>
                    <div className='footerbox'>
                        <Button type='primary' size='large' htmlType="submit" style={{ marginRight: 10, marginLeft: 10 }} className='save'>保存</Button>
                        <Button type='primary' size='large'>预览</Button>
                    </div>
                </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ArticlePost;
