import React from 'react';
import { Input,Rate,Button,Form,message,Modal } from 'antd';
import { createHashHistory  } from 'history'
import { Link } from 'react-router-dom';
import axios from 'axios';

const { TextArea } = Input;
const FormItem = Form.Item;
const history = createHashHistory();
const _success = () => {
    message.success('New comment successfully submit');
  };
  const _error = () => {
    message.error('New comment failed');
  };

export default class ArticleDetailComment extends React.Component {

    showModal = () => {
        this.setState({
          visible: true,
        });
      }
    
    handleOk = () => {
        this.setState({
        visible: false
        });
    }
    
    handleCancel = () => {
        this.setState({
        visible: false,
        });
    }
    
    changeEvent = (e) =>{
        this.setState({token: e.target.value})
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
        if (!err) {
            const submitData = {
            name: this.props.name,
            rate: values.rate,
            comment: values.comment
            }
            axios.post('https://z0h6x1r9y5.execute-api.us-east-1.amazonaws.com/stage/addarticlecomment',submitData)
            .then(function (response) {
                _success();
                history.push('/blog')
            }).catch(function (error) {
                console.log(error);
                _error()
            })
            }
    })
    }

    render() {
        const { getFieldDecorator } = this.props.form;
            return (
                <div className="article-comment-box">
                <Form onSubmit={this.handleSubmit}>
                    <div className="article-comment-box-rate">
                    <FormItem>{getFieldDecorator('rate', {
                                  defaultValue:2.5})(
                            <Rate allowHalf/>
                        )}
                        </FormItem>
                    </div>
                
                     <div className="article-comment-box-coment">
                        <FormItem> {getFieldDecorator('comment', {
                                  defaultValue:'no comments'})(
                            <TextArea placeholder="comments"  style={{ width: '80%','textAlign': 'center' }}
                             autosize={{ minRows: 2, maxRows: 6 }} />
                        )}
                        </FormItem>
                     </div>
                     
                     <div  className="article-comment-box-button">
                     <Button type="primary" htmlType="submit" >submit</Button>&nbsp;&nbsp;&nbsp;
                    <Link to="/blog"><Button>cancel</Button></Link></div>
                </Form>
                </div>
            );
        }
}
ArticleDetailComment = Form.create()(ArticleDetailComment);
