import React from 'react';
import axios from 'axios';
import { Row, Col } from 'antd';
import { Spin,Icon } from 'antd';
import ArticleArea from './articlearea';
import RightSider from './rightsider';

const antIcon = <Icon type="loading"/>;

class BlogIndex extends React.Component {

  constructor() {
    super();
    this.state = {
      loadArticle: false,
      loadCategory: false,
      articles: [],
      categoryList: []
    }
  }

  componentDidMount() {
    const _this = this;
    axios.post('https://z0h6x1r9y5.execute-api.us-east-1.amazonaws.com/stage/getarticleall')
      .then(function (response) {
        _this.setState({
          articles: response.data
        });
      }).catch(function (error) {
        console.log(error);
        _this.setState({
          error: error
        })
      })

      axios.post('https://z0h6x1r9y5.execute-api.us-east-1.amazonaws.com/stage/getarticlecategory')
      .then(function (response) {
        _this.setState({
          categoryList: response.data
        });
      }).catch(function (error) {
        console.log(error);
        _this.setState({
          error: error
        })
      })

  }
  render() {
    var loadArticle = this.state.loadArticle;
    var loadCategory = this.state.categoryList;
    if(loadArticle&&loadCategory){
      return (
        <div className="main">
          <div className="main-container">
            <Row>
                <Col xs={24} sm={24} md={18}><ArticleArea articles={this.state.articles} /></Col>
                <Col xs={0} sm={0} md={6}><RightSider categoryList={this.state.categoryList}/></Col>
            </Row>
          </div>
        </div>
      );
    }else{
      return (
          <div>
            <Spin indicator={antIcon}/> 
          </div>
      )
    }
  }
}

export default BlogIndex;
