import React from 'react';
import { Icon } from 'antd';
import * as formatContent from '../../util/formatWbsContent.js';

const getWbContent = (content) => {
  let subContent = content.substring(0,10);
  return <div className="blog-article-item-desc" dangerouslySetInnerHTML={formatContent.createMarkup(subContent)}/>;
}

export default class ArticleItem extends React.Component {
  render() {
    return (
      <div className="blog-article-item">
        <div className="blog-article-item-title">{this.props.title}</div>
        <div className="blog-article-item-time"><Icon type="calendar" /> {this.props.time}</div>
         {getWbContent(this.props.content)}
      </div>
    );
  }
}
