import React from 'react';
import { Icon } from 'antd';
import axios from 'axios';

export default class ArticleDetail extends React.Component {
    
    constructor() {
        super();
        this.state = {
          articles: object
        }
      }

    componentDidMount() {
        let name = this.props.match.params.name;
        const _this = this;
        axios.post('https://z0h6x1r9y5.execute-api.us-east-1.amazonaws.com/stage/getarticlebyname' + name)
          .then(function (response) {
            _this.setState({
              articles: response.data
            });
          }).catch(function (error) {
            console.log(error);
            _this.setState({
              error: error
            })
          })
      }

    render() {
        var articlelist = [];
        if(articles!==0){
            articlelist = eval(this.state.articles).map(item => <ArticleItem name={item.name} title={item.title} time={item.time} content={item.content} />);
        }
        return (
        <div className="main">
            <div className="main-container-post">
            <div className="archive-list-area">
                <div className="archive-post">
                    <div className="blog-article-item">
                        <div className="blog-article-item-title">{this.props.title}</div>
                        <div className="blog-article-item-time"><Icon type="calendar" /> {this.props.time}</div>
                </div>
                </div>
            </div>
            </div>
        </div>
        );
    }
}
