import React from 'react'
import ArticleItem from './articleitem';

export default class ArticleList extends React.Component {
  render() {
    var articlelist = [];
    const articles = this.props.articles;

    if(this.props.type)
    if(articles!==0){
      articlelist = eval(articles).map(item => <ArticleItem key={item.name} name={item.name} title={item.title} time={item.time} content={item.content} />);
  }

    return (
      <div className="archive-list">
        {articlelist}
      </div>
    );
  }
}
