import React from 'react';
import { Link } from 'react-router-dom';
import { createHashHistory  } from 'history'
import CategoryCard from './categorycard';
import '../../../css/blog/tagcard.css';

const ArchiveCard = () => (
  <div className="blog-rightsider-archive">
    <Link to="/blog" onClick={() => handleClick()}>所有文章</Link>
  </div>
);
const history = createHashHistory();

const handleClick = () => {
  history.push('/blog')
}


export default class RightSider extends React.Component {
  render() {
    return (
      <div className="rightsider">
        <ArchiveCard />
        <CategoryCard categoryList={this.props.categoryList} />
      </div>
    );
  }
}
