import React from 'react'
import ArticleItem from './articleitem';
import CategoryItem from './categoryitem';

export default class ArticleList extends React.Component {
  render() {
    var list = [];
    const articles = this.props.articles;
    const articlescategory = this.props.articlescategory;

    if(this.props.type==='article'){
      if(articles!==0){
        list = eval(articles).map(item => <ArticleItem key={item.name} name={item.name} title={item.title} time={item.time} content={item.content} />);
      }else{
        if(articles!==0){
          list = eval(articlescategory).map(item => <CategoryItem key={item.name} name={item.name} title={item.title} time={item.time} />);
      }
    }
  }

    return (
      <div className="archive-list">
        {list}
      </div>
    );
  }
}
