import React from 'react';
import PropTypes from 'prop-types';
import { Row, Col } from 'antd';
import { connect } from 'react-redux';
import ArticleArea from './articlearea';
import RightSider from './rightsider';

class BlogIndex extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      users: [],
      isLoaded: false
    }
  }


  //当组件输出到 DOM 后会执行 componentDidMount() 
  componentDidMount() {
    const _this = this; //先存一下this，以防使用箭头函数this会指向我们不希望它所指向的对象。 
    axios.get('https://5b5e71c98e9f160014b88cc9.mockapi.io/api/v1/lists')
      .then(function (response) {
        _this.setState({
          users: response.data,
          isLoaded: true
        });
      }).catch(function (error) {
        console.log(error);
        _this.setState({
          isLoaded: false, error: error
        })
      })

  }
  render() {
    return (
      <div className="main">
        <div className="main-container">
          <Row>
            <Col xs={24} sm={24} md={18}><ArticleArea issues={this.props.items} /></Col>
            <Col xs={0} sm={0} md={6}><RightSider issues={this.props.items} /></Col>
          </Row>
        </div>
      </div>
    );
  }
}

BlogIndex.defaultProps = {
  dispatch: null,
  isFetching: true,
  items: [],
};

BlogIndex.propTypes = {
  dispatch: PropTypes.func,
  isFetching: PropTypes.bool,
  items: PropTypes.array,
};

function mapStateToProps(state) {
  const { isFetching, items } = state || { isFetching: true, items: [] };
  return { isFetching, items };
}

export default connect(mapStateToProps)(BlogIndex);
