import React from 'react';
import axios from 'axios';
import { Icon } from 'antd';
import { Link } from 'react-router-dom';

export default class CategoryCard extends React.Component {

  constructor() {
    super();
    this.state = {
      categoryList: []
    }
  }

  componentDidMount() {
    const _this = this;
    axios.post('https://z0h6x1r9y5.execute-api.us-east-1.amazonaws.com/stage/getarticlecategory')
      .then(function (response) {
        _this.setState({
          categoryList: response.data
        });
      }).catch(function (error) {
        console.log(error);
        _this.setState({
          error: error
        })
      })
  }

  render() {
    var categoryLinkList = [];
    if(this.state.categoryList.length!==0){
        categoryLinkList = eval(this.state.categoryList).map(
        item => <Link key={item.name} to={`/blog/category/${item.name}`}><li key={item.name}>{item.name}</li></Link>);
    }
    return (
      <div className="rightsider-card">
        <div className="rightsider-card-title"><Icon type="bars" /> 分类</div>
        <div className="rightsider-card-content">
          <ul>
            {categoryLinkList}
          </ul>
        </div>
      </div>
    );
  }
}
